        let mut sha1 = Sha1::new();
        sha1.update(signed_attributes);
        let res = sha1.finalize().to_vec();

        let signature = config
            .device_private_key
            .sign(PaddingScheme::new_pkcs1v15_sign(Some(rsa::hash::Hash::SHA1)), &res)
            .unwrap();
        println!("{:?}", signature);
